import * as R from 'ramda'

import phones from 'api/mockPhones'
import categories from 'api/mockCategories'

export let fetchPhones = async () => {
    return new Promise((resolve, reject) => {
        resolve(phones)
    })
}

export let loadMorePhones = async offset => {
    return new Promise((resolve, reject) => {
        setTimeout(() => resolve(phones), 1000)
    })
}

export let fetchPhoneById = async id => {
    return new Promise((resolve, reject) => {
        const phone = R.find(R.propEq('id', id), phones)
        resolve(phone)
    })
}

export let fetchCategories = async() => {
    return new Promise((resolve, reject) => {
        resolve(categories)
    })
}