import * as R from 'ramda'

import {
    FETCH_CATEGORIES_SUCCESS
} from 'actionTypes'

let initialState = {}

export let categories = (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_CATEGORIES_SUCCESS:
            const newValues = R.indexBy(R.prop('id'), payload)
            return R.mergeLeft(newValues, state)
        default:
            return state
    }
}