import {
    FETCH_PHONE_BY_ID_SUCCESS
} from 'actionTypes'
import * as R from 'ramda'

let initialState = {
    id: null
}

export let phonePage = (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONE_BY_ID_SUCCESS:
            return R.mergeLeft({
                id: R.prop('id', payload)
            }, state)
        default:
            return state
    }
}