import { FETCH_PHONES_SUCCESS} from 'actionTypes'
import * as R from 'ramda'

let initialState = {
    ids: []
}

export let phonesPage = (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            return R.mergeAll({
                ids: R.pluck('id', payload)
            }, state)
        default:
            return state
    }
}