import { 
    FETCH_PHONES_SUCCESS,
    LOAD_MORE_PHONES_SUCCESS,
    SEARCH_PHONE
} from 'actionTypes'
import * as R from 'ramda'

let initialState = {
    ids: [],
    search: ''
}

export let phonesPage = (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            return R.mergeLeft({
                ids: R.pluck('id', payload)
            }, state)
        case LOAD_MORE_PHONES_SUCCESS:
            const ids = R.pluck('id', payload)
            return R.mergeLeft({
                ids: R.concat(state.ids, ids)
            }, state)
        case SEARCH_PHONE:
            return R.mergeLeft({
                search: payload
            }, state)
        default:
            return state
    }
}